import React, { useState } from "react";
import { useContext } from "react";
import TaskContext from "../context/TaskContex";

function TaskSearch() {
  const { setTaskList } = useContext(TaskContext);
  const { taskList } = useContext(TaskContext);

  const [searchTask, setSearchTask] = useState("");

  const filteredTasks = taskList.filter((item) => {
    return item.title.toLocaleLowerCase().includes(searchTask);
  });

  const formClassName = "form-area";
  return (
    <div className={formClassName}>
      <form>
        <h3>Search Task</h3>
        <input
          type="text"
          value={searchTask}
          onChange={(e) => {
            const searchTaskFilter = e.target.value.toLocaleLowerCase();
            setTaskList(searchTaskFilter);
            setSearchTask(e.target.value);
          }}
          placeholder="Enter title.."
        />
      </form>
      <br/>
    </div>
  );
}

export default TaskSearch;
