import { createContext, useState } from "react";

const TaskContext = createContext();

function Provider({ children }) {
  const [taskList, setTaskList] = useState([]);

  const createTask = (title, taskDetail) => {
    const newTasksList = [
      ...taskList,
      {
        id: Math.round(Math.random() * 999999),
        title,
        taskDetail: taskDetail.trim()
      },
    ];

    localStorage.setItem("tasks",JSON.stringify(newTasksList))
    setTaskList(newTasksList);
  };

  const onDelete = (id) => {
    const newTasksList = taskList.filter((item) => item.id !== id);
    localStorage.setItem("tasks",JSON.stringify(newTasksList))
    setTaskList(newTasksList);
  };

  const onUpdate = (id, title, taskDetail) => {
    const newTasksList = taskList.map((item) => {
      if (item.id === id) {
        return { id, title, taskDetail };
      }
      return item;
    });
    localStorage.setItem("tasks",JSON.stringify(newTasksList))
    setTaskList(newTasksList);
  };

  const fetchTasks = () =>{
    let tasks;
    if(localStorage.getItem("tasks")){
      tasks = JSON.parse(localStorage.getItem("tasks"))
      console.log(tasks)
    }else{
      localStorage.setItem("tasks",JSON.stringify([]))
      tasks = JSON.parse(localStorage.getItem("tasks"))
      console.log(tasks)
    }
    setTaskList(tasks)
  }

  const sharedValuesAndMethods = {
    taskList,
    createTask,
    onDelete,
    onUpdate,
    fetchTasks,
  };

  return (
    <TaskContext.Provider value={sharedValuesAndMethods}>
      {children}
    </TaskContext.Provider>
  );
}

export { Provider };
export default TaskContext;
