import React, { useState } from "react";
import TaskCreate from "./TaskCreate";
import { useContext } from "react";
import TaskContext from "../context/TaskContex";
function TaskItem({ task }) {
  const { onDelete, onUpdate } = useContext(TaskContext);

  const [showEdit, setShowEdit] = useState(false);
  const handleOnDelete = () => {
    onDelete(task.id);
  };

  const handleOnEdit = () => {
    setShowEdit(!showEdit);
  };

  const handleUpdate = (id, title, taskDetail) => {
    setShowEdit(false);
    onUpdate(id, title, taskDetail);
  };
  return (
    <>
          <TaskCreate task={task} taskFormEdit={true} onUpdate={handleUpdate} />
        <div className="task-box">
          <p className="task-id">
            <span>#</span>
            {task.id}
          </p>
          <label className="label-text">Task Title</label>
          <p className="task-text">{task.title}</p>
          <label className="label-text">Task Detail</label>
          <p className="task-text">{task.taskDetail}</p>

          <div className="task-buttons">
            <button onClick={handleOnDelete} className="delete-btn">
              Delete
            </button>
            <button onClick={handleOnEdit} className="edit-btn">
              Edit
            </button>
          </div>
        </div>
    </>
  );
}

export default TaskItem;
