import React, { useState } from "react";

export default function TaskUpdate({ task, taskFormEdit, onUpdate }) {
  const [title, setTitle] = useState(task ? task.title : "");
  const [taskDetail, setTaskDetail] = useState(task ? task.taskDetail : "");

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(task.id, title, taskDetail)
    setTitle("");
    setTaskDetail("");
  };
  return (
    <div>
      <div className="form-area edit">
        <form>
          <h3>{taskFormEdit ? "Please Edit Task" : "Please Add a Task"}</h3>
          <label>Task Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter title.."
          />
          <label>Task Details</label>
          <textarea
            rows="3"
            cols="2"
            placeholder="Enter task detail.."
            value={taskDetail}
            onChange={(e) => setTaskDetail(e.target.value)}
          ></textarea>
          {taskFormEdit ? (
            <button onClick={handleSubmit} type="submit">
              Edit Task
            </button>
          ) : (
            <button onClick={handleSubmit} type="submit">
              Add Task
            </button>
          )}
        </form>
      </div>
    </div>
  );
}
