import { useState } from 'react';
import TaskCreate from './components/TaskCreate';
import TaskList from './components/TaskList';
import './index.css';

function App() {

  const [taskList, setTaskList] = useState([])

  const createTask = (title,taskDetail) => {
    const newTasksList = [
      ...taskList, {
        id: Math.round(Math.random()*999999),
        title,
        taskDetail
      } 
    ]

    setTaskList(newTasksList)
  }

  const onDelete = (id) => {
    const newTaskList = taskList.filter(item => item.id !== id)
    setTaskList(newTaskList)
  }

  const onUpdate = (id,title,taskDetail) => {
    const newTaskList = taskList.map(item => {
      if(item.id === id){
        return {id,title,taskDetail}
      }
      return item;
    })
    setTaskList(newTaskList)
  }

  return (
    <div className="app-container">
      <TaskCreate onCreate = {createTask}/>
      <h3>GÃ¶revler</h3>
      <TaskList taskList = {taskList} onDelete = {onDelete} onUpdate = {onUpdate}/>
    </div>
  );
}

export default App;
