import { createContext, useState } from "react";

const TaskContext = createContext();

function Provider({ children }) {
  const [taskList, setTaskList] = useState([]);
  const [searchTask, setSearchTask] = useState("");

  const createTask = (title, taskDetail) => {
    const newTasksList = [
      ...taskList,
      {
        id: Math.round(Math.random() * 999999),
        title,
        taskDetail,
      },
    ];

    setTaskList(newTasksList);
  };

  const onDelete = (id) => {
    const newTaskList = taskList.filter((item) => item.id !== id);
    setTaskList(newTaskList);
  };

  const onUpdate = (id, title, taskDetail) => {
    const newTaskList = taskList.map((item) => {
      if (item.id === id) {
        return { id, title, taskDetail };
      }
      return item;
    });
    setTaskList(newTaskList);
  };

  const fetchTasks = () =>{
    const dummyTasks = [
        {
            id: 12257997,
            title: "Task Dummy Title",
            taskDetail: "Task Dummy Detail"
        },
        {
            id: 973956,
            title: "Task Dummy Title 2",
            taskDetail: "Task Dummy Detail 2"
        },
        {
            id: 105693,
            title: "Task Dummy Title 3",
            taskDetail: "Task Dummy Detail 3"
        }
    ]

    setTaskList(dummyTasks)
  }

  const filterTasks = (e) => {
    const filteredTasks = taskList.filter((item) => {
      return item.title.toLocaleLowerCase().includes(searchTask);
    });

    const searchTaskFilter = e.target.value.toLocaleLowerCase();
            setSearchTask(e.target.value);
  }

  const sharedValuesAndMethods = {
    filterTasks,
    taskList,
    createTask,
    onDelete,
    onUpdate,
    fetchTasks,
  };

  return (
    <TaskContext.Provider value={sharedValuesAndMethods}>
      {children}
    </TaskContext.Provider>
  );
}

export { Provider };
export default TaskContext;
