import React from "react";
import TaskItem from "./TaskItem";
import { useContext } from 'react'
import TaskContext from '../context/TaskContex';
function TaskList() {
  const { taskList , currentStatus, changeStatus} = useContext(TaskContext)
  const showTaskList = () => {
    const filteredList = taskList.filter(item => item.status === currentStatus);
    console.log(filteredList)
    return taskList.length > 0 ? taskList.map((task, index) => {
      if(currentStatus === task.status || currentStatus === "all"){
        return (
          <TaskItem
            key={index}
            task={task}
          />
        );
      } else {
        return ""
      }

    }) : <p className="empty-message">You have no {currentStatus} task yet</p>
  }
  return (
    <div className="tasks">
      <div className="filter">
        <button onClick={() => changeStatus("all")} type="button" className={currentStatus === "all" ? "filter-btn active" : "filter-btn"}>All</button>
        <button onClick={() => changeStatus("pending")} type="button" className={currentStatus === "pending" ? "filter-btn active" : "filter-btn"}>Pending</button>
        <button onClick={() => changeStatus("completed")} type="button" className={currentStatus === "completed" ? "filter-btn active" : "filter-btn"}>Completed</button>
      </div>
      <h3 className="title">{currentStatus} Task List</h3>
      <div className="task-list">
        {
          showTaskList()
        }
      </div>
    </div>
  );
}

export default TaskList;
